contract Vulcan {
    address public creator;
    //immutables
    IERC20 public immutable token;

    // struct for token info
    struct TOKEN {
        string name;
        string symbol;
        uint256 totalSupply;
        address tokenAddress;
        uint256 decimal;
        uint256 price;
    }
    // struct for project info
    struct PROJECT {
        string title;
        string description;
        string logo;
        string videoLink;
        uint256 softcap;
        uint256 hardcap;
        uint256 endTime;
        address owner;
    }
    //project info
    PROJECT public projectInfo;
    //token info
    TOKEN public tokenInfo;
    //funds raised from ICO
    uint256 public fundsRaised;
    // Tracks contributions of investors
    mapping(address => uint256) public contributions;

    // event Withdrawal(uint amount, uint when);

    constructor(
        string memory title_,
        string memory description_,
        string memory logo_,
        string memory videoLink_,
        uint256 softcap_,
        uint256 hardcap_,
        uint256 endTime_,
        string memory name_,
        string memory symbol_,
        address owner_,
        uint256 price_,
        uint256 decimal_,
        uint256 totalSupply_,
        address tokenAddress_
    ) payable {
        require(block.timestamp < endTime_, "End time should be in the future");
        require(hardcap_ > softcap_, "Invalid hardcap & softcap setting");
        require(tokenAddress_ != address(0), "invalid token address");

        projectInfo.title = title_;
        projectInfo.description = description_;
        projectInfo.logo = logo_;
        projectInfo.videoLink = videoLink_;
        projectInfo.softcap = softcap_;
        projectInfo.hardcap = hardcap_;
        projectInfo.endTime = endTime_;
        projectInfo.owner = owner_;

        tokenInfo.name = name_;
        tokenInfo.totalSupply = totalSupply_;
        tokenInfo.symbol = symbol_;
        tokenInfo.tokenAddress = tokenAddress_;
        tokenInfo.price = price_;
        tokenInfo.decimal = decimal_;

        token = IERC20(tokenAddress_);
        creator = msg.sender;
    }
    /**
     * @dev return remaining token balance for ICO
     * @return amount token balance as uint256
     */
    function getRemainAmount() public view returns (uint256) {
        uint256 amount = token.balanceOf(address(this));
        return amount;
    }

    function owner() public view returns (address) {
        return projectInfo.owner;
    }
}

