import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { Vulcan } from "./Vulcan.sol";
// Uncomment this line to use console.log
// import "hardhat/console.sol";

contract VulcanPadFactory {
    //owner of the factory
    address public owner;
    //immutables
    IERC20 public immutable daiToken;
    address public daiAddress;
    //spam filter fee amount 100DAI as decimal is 18
    uint256 public feeAmount = 100 ether; 
    // Tracks spam filter fee contributions of investors
    mapping(address => uint256) feeContributions; 
    //created ICOs
    address[] public vulcans;
    mapping(address => bool) isVulcan;
    //events
    event SpamFilterFeePaid(address user_, uint256 amount_);
    // event ICOCreated(address user, string title, string description, string logo, string videoLink, uint256 softcap, uint256 hardcap, uint256 endTime, address tokenAddress, string name, string symbol, uint256 price, uint256 decimal, uint256 totalSupply, address address);
    //modifiers
    modifier notZeroAddress(address token_) {
        require (token_ != address(0), "Invalid TOKEN address");
        _;
    }
    modifier PaidSpamFilterFee(address user_) {
        require(
            feeContributions[user_] >= feeAmount,
            "Not paid spam filter fee"
        );
        _;
    }
    modifier notFuture(uint256 endTime_) {
        require(block.timestamp < endTime_, "End time should be in the future");
        _;
    }
    modifier invalidCapSetting(uint256 softcap_, uint256 hardcap_) {
        require(softcap_ > 0, "Softcap must be greater than 0");
        require(hardcap_ > 0, "Hardcap must be greater than 0");
        require(hardcap_ > softcap_, "Softcap must less than hardcap");
        _;
    }
    modifier notZeroPrice(uint256 price_) {
        require(price_ > 0, "Token price must greater than 0");
        _;
    }
    modifier notZeroDecimal(uint256 decimal_) {
        require(decimal_ > 0, "Token decimal must greater than 0");
        _;
    }
    modifier notZeroTotalSupply(uint256 totalSupply_) {
        require(totalSupply_ > 0, "Token totalSupply must greater than 0");
        _;
    }
    /**
     * @dev contructor
     * @param daiAddress_ DAI stable coin address for paying spam filter fee...
     */
    constructor(address daiAddress_) {
        require(daiAddress_ != address(0), "Invalid DAI address");
        daiToken = IERC20(daiAddress_);
        owner = msg.sender;
    }
    /**
     * @dev Pays non-refundable Spam filter fee 100DAI
     */
    function paySpamFilterFee() external {
        uint256 _balance = daiToken.balanceOf(msg.sender);
        require(_balance > feeAmount, "Insufficient Dai balance");
        bool _success = daiToken.approve(address(this), feeAmount);
        require(_success, "DAI approve failed");
        SafeERC20.safeTransferFrom(daiToken, msg.sender, address(this), feeAmount);
        feeContributions[msg.sender] += feeAmount;
        emit SpamFilterFeePaid (msg.sender, feeAmount);
    }
    /**
     * @dev launch new ICO
     * @param title_ project title "Vulcan launchpad"
     * @param description_ project description "this is highly..."
     * @param logo_ logo image "https://..."
     * @param videoLink_ introduction video link "https://youtube..."
     * @param softcap_ softcap for ICO 100 * 10**18
     * @param hardcap_ hardcap for ICO  200 * 10**18
     * @param endTime_ ICO end time 1762819200000
     * @param name_ token name "vulcan token"
     * @param symbol_ token symbol "$VULCAN"
     * @param price_ token price for ICO 0.01 * 10**18
     * @param decimal_ token decimal 18
     * @param totalSupply_ token totalSupply 1000000000 * 10**18
     * @param tokenAddress_ token address 0x810fa...
     */
    function launchNewICO(
        string memory title_,
        string memory description_,
        string memory logo_,
        string memory videoLink_,
        uint256 softcap_,
        uint256 hardcap_,
        uint256 endTime_,
        string memory name_,
        string memory symbol_,
        uint256 price_,
        uint256 decimal_,
        uint256 totalSupply_,
        address tokenAddress_
    )
        public
        PaidSpamFilterFee(msg.sender)
        notZeroAddress(tokenAddress_)
        notFuture(endTime_)
        invalidCapSetting(softcap_, hardcap_)
        notZeroDecimal(decimal_)
        notZeroPrice(price_)
        notZeroTotalSupply(totalSupply_)
        returns (address)
    {
        Vulcan _newVulcan = new Vulcan(
            title_,
            description_,
            logo_,
            videoLink_,
            softcap_,
            hardcap_,
            endTime_,
            name_,
            symbol_,
            msg.sender,
            price_,
            decimal_,
            totalSupply_,
            tokenAddress_
        );
        address _vulcan = address (_newVulcan);
        vulcans.push(_vulcan);
        isVulcan[_vulcan] = true;
        // emit ICOCreated (msg.sender, title, description, logo, videoLink, softcap, hardcap, endTime, tokenAddress, name, symbol, price, decimal, totalSupply, address);
        return _vulcan;
    }   
    /**
     * @dev send token to sale for ICO to created Vulcan contract
     * @param vulcanAddress_ created vulcan contract address
     * @param tokenAddress_ ERC20 token address
     * @param amount_ token amount to send
     */
    function depositToken (address vulcanAddress_, address tokenAddress_, uint256 amount_) public {

        Vulcan _vulcan = Vulcan (vulcanAddress_);
        address _owner = _vulcan.owner ();
        require (_owner == msg.sender, "You are not the owner of Vulcan contract."); // only owner of vulcan can depost token

        IERC20 _token = IERC20 (tokenAddress_);
        uint256 _balance = _token.balanceOf(msg.sender);
        require (_balance > amount_, "Insufficient token balance."); // balance must be greater than amount to deposit

        bool _success = _token.approve(address(this), amount_);
        require(_success, "Token approve faild.");

        SafeERC20.safeTransferFrom(_token, msg.sender, vulcanAddress_, amount_);

    }

}

